mapper.delete:
delete from subscribe
where user_id = #{userId}
and companyCode = #{companyCode}

mapper.select:
select message_id id, user_id, companyCode, content, date, user_name from message join user_tb on message.user_id = user_tb.id where companyCode = #{companyCode}
and date > (select date from subscribe where companyCode = #{companyCode} and user_id = #{userId})
order by id desc limit 20

select v.companyCode, v.companyName, count(user_id) countUser from subscribe s join domestic_stock_code v on s.companyCode = v.companyCode
where companyName like concat('%',#{searchWord},'%')
group by companyCode, companyName limit #{offset}, #{recordSize}

select concat(s.advisor_id,'@',s.user_id) companyCode, advisor_full_name companyName, s.user_id from subscribe_to_advisor s
join advisor a on a.advisor_id = s.advisor_id
where s.user_id = {userId}
